            <p className="title">{data.news.title}</p>
            <p className="show">
              <a href={`/news/${data.news.url}`}>Xem thÃªm</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

SingerPost.propTypes = propTypes;

export default SingerPost;
